package de.persosim.simulator.perso;

import java.io.StringReader;
import java.security.KeyPair;

import de.persosim.simulator.crypto.CryptoUtil;
import de.persosim.simulator.utils.HexString;

/**
 * @author slutters
 *
 */
public class Profile03 extends AbstractProfile {
	
	@Override
	public void setPersoDataContainer() {
		persoDataContainer = PersonalizationDataContainer.getDefaultContainer();
		persoDataContainer.setDg3PlainData("20340630");
		persoDataContainer.setDg4PlainData("JOHANNA EDELTRAUT LISBETH");
		persoDataContainer.setDg5PlainData("MUSTERMANN");
		persoDataContainer.setDg6PlainData("ORDENSSCHWESTER JOHANNA");
		persoDataContainer.setDg7PlainData("DR.");
//		persoDataContainer.setDg8PlainData("19280421");
		persoDataContainer.setDg8PlainData("19680421");
		persoDataContainer.setDg9PlainData("MÜNCHEN");
		persoDataContainer.setDg10PlainData("D");
		persoDataContainer.setDg13PlainData("VON MÜLLER-SCHWARZENBERG");
		persoDataContainer.setDg17StreetPlainData(" BOUCHÉSTR. 68 A");
		persoDataContainer.setDg17CityPlainData("BERLIN");
		persoDataContainer.setDg17CountryPlainData("D");
		persoDataContainer.setDg17ZipPlainData("12059");
		persoDataContainer.setDg18PlainData("02761100000000");
		persoDataContainer.setEfCardAccess("");
		persoDataContainer.setEfCardSecurity("");
		persoDataContainer.setEfChipSecurity("");
		
		String documentNumber = "000000003";
		String sex = "F";
		String mrzLine3 = "MUSTERMANN<<JOHANNA<EDELTRAUT<";
		String mrz = persoDataContainer.createMrzFromDgs(documentNumber, sex, mrzLine3);
		
		persoDataContainer.setMrz(mrz);
		persoDataContainer.setEpassDg1PlainData(mrz);
		
		// unprivileged CA key
		persoDataContainer.addCaKeyPair(CryptoUtil.reconstructKeyPair(13,
				HexString.toByteArray("0419D4B7447788B0E1993DB35500999627E739A4E5E35F02D8FB07D6122E76567F17758D7A3AA6943EF23E5E2909B3E8B31BFAA4544C2CBF1FB487F31FF239C8F8"),
				HexString.toByteArray("A07EB62E891DAA84643E0AFCC1AF006891B669B8F51E379477DBEAB8C987A610")),
				41, false);
		
		// privileged CA key
		persoDataContainer.addCaKeyPair(CryptoUtil.reconstructKeyPair(13,
				HexString.toByteArray("04992622DD06173C593D84A07B9EFBCAD889483223063E3613A982503EE34285E60A10C5F0DACE211A769947C6986A415DB5FFE624F9C2961570E475A1201F35C1"),
				HexString.toByteArray("861BA9BF16AF946D48AD045C5B55044F188E4A4633CDF5A4E438EC016C5A8719")),
				45, true);

		// individual RI key - 1st sector public/private key pair (Sperrmerkmal)
		KeyPair riKeyPair1 = (KeyPair)PersonalizationFactory.unmarshal(new StringReader("""
				<java.security.KeyPair id="1">
				  <privateKey id="2">
				    <algorithm>ECDH</algorithm>
				    <value></value>
				  </privateKey>
				  <publicKey id="3">
				    <algorithm>ECDH</algorithm>
				    <value></value>
				  </publicKey>
				</java.security.KeyPair>						
		"""));
		persoDataContainer.addRiKeyPair(riKeyPair1, 1, false);

		// individual RI key - 2nd sector public/private key pair (Pseudonym)
		KeyPair riKeyPair2 = (KeyPair)PersonalizationFactory.unmarshal(new StringReader("""
				<java.security.KeyPair id="1">
				  <privateKey id="2">
				    <algorithm>ECDH</algorithm>
				    <value></value>
				  </privateKey>
				  <publicKey id="3">
				    <algorithm>ECDH</algorithm>
				    <value></value>
				  </publicKey>
				</java.security.KeyPair>						
		"""));
		persoDataContainer.addRiKeyPair(riKeyPair2, 2, true);
	}

}
