/**
 * @author slutters
 *
 */
public class Profile10 extends AbstractProfile {
	
	@Override
	public void setPersoDataContainer() {
		persoDataContainer = PersonalizationDataContainer.getDefaultContainer();
		persoDataContainer.setDg3PlainData("20340630");
		persoDataContainer.setDg4PlainData("AFGFGFGFGFGFGFGFGFGFGFGFGFGFGFGFGFGFGFGFGFGFGFGFGFGFGFGFGFGFGFGFGFGFGFGFGFGFGFGZ");
		persoDataContainer.setDg5PlainData("ABCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCBCZ");
		persoDataContainer.setDg6PlainData("AJKJKJKJKJKJKJKJKJKJKJKJKJKJKJKZ");
		persoDataContainer.setDg7PlainData("AHIHIHIHIHIHIHIHIHIHIHIHIHIHIHIZ");
		persoDataContainer.setDg8PlainData("20000101");
		persoDataContainer.setDg9PlainData("ALMLMLMLMLMLMLMLMLMLMLMLMLMLMLMLMLMLMLMLMLMLMLMLMLMLMLMLMLMLMLMLMLMLMLMLMLMLMLMZ");
		persoDataContainer.setDg10PlainData("D");
		persoDataContainer.setDg13PlainData("ADEDEDEDEDEDEDEDEDEDEDEDEDEDEDEDEDEDEDEDEDEDEDEDEDEDEDEDEDEDEDEDEDEDEDEDEDEDEDEZ");
		persoDataContainer.setDg17StreetPlainData("APQPQPQPQPQPQPQPQPQPQPQPQPQPQPQPQPQPQPQPQPQPQPQPQZ");
		persoDataContainer.setDg17CityPlainData("ANONONONONONONONONONONONONONONONONONONOZ");
		persoDataContainer.setDg17CountryPlainData("D");
		persoDataContainer.setDg17ZipPlainData("A12345678Z");
		persoDataContainer.setDg18PlainData("02761100000000");
		persoDataContainer.setEfCardAccess("");
		persoDataContainer.setEfCardSecurity("");
		persoDataContainer.setEfChipSecurity("");
		
		String documentNumber = "000000010";
		String sex = "F";
		String mrzLine3 = "MUELLER<<HILDEGARD<<<<<<<<<<<<";
		String mrz = persoDataContainer.createMrzFromDgs(documentNumber, sex, mrzLine3);
		
		persoDataContainer.setMrz(mrz);
		persoDataContainer.setEpassDg1PlainData(mrz);
		
		// unprivileged CA key
		persoDataContainer.addCaKeyPair(CryptoUtil.reconstructKeyPair(13,
				HexString.toByteArray("0467DBFBD14C3291267FEFF537062570B96BE2274D7747D734BBDB5BFEAAD0976C3E47B929F42B1FCD583F80FB469225E29FE00AC6C95C24E956CB8E7031C19AC2"),
				HexString.toByteArray("8910074CF4749A916E5864654C768D57F57B6361F70A226DD1AEBED390BB066D")),
				41, false);
		
		// privileged CA key
		persoDataContainer.addCaKeyPair(CryptoUtil.reconstructKeyPair(13,
				HexString.toByteArray("0465F192A4270BB47C0C05BB4FDE29DB38031082F0DD830D533C3D0EB52F31F1249E6773E2202C7E168A20A1F2CAD4ED61766C697C3E3533916ADE402B412DAEA5"),
				HexString.toByteArray("46A957BAE8EA7D99183CAC13345CE667EC2F76D70E0095CE15D01F2686C3BD64")),
				45, true);

		// individual RI key - 1st sector public/private key pair (Sperrmerkmal)
		KeyPair riKeyPair1 = (KeyPair)PersonalizationFactory.unmarshal(new StringReader("""
				<java.security.KeyPair id="1">
				  <privateKey id="2">
				    <algorithm>ECDH</algorithm>
				    <value></value>
				  </privateKey>
				  <publicKey id="3">
				    <algorithm>ECDH</algorithm>
				    <value></value>
				  </publicKey>
				</java.security.KeyPair>						
		"""));
		persoDataContainer.addRiKeyPair(riKeyPair1, 1, false);

		// individual RI key - 2nd sector public/private key pair (Pseudonym)
		KeyPair riKeyPair2 = (KeyPair)PersonalizationFactory.unmarshal(new StringReader("""
				<java.security.KeyPair id="1">
				  <privateKey id="2">
				    <algorithm>ECDH</algorithm>
				    <value></value>
				  </privateKey>
				  <publicKey id="3">
				    <algorithm>ECDH</algorithm>
				    <value></value>
				  </publicKey>
				</java.security.KeyPair>						
		"""));
		persoDataContainer.addRiKeyPair(riKeyPair2, 2, true);
	}

}
