package de.persosim.simulator.perso;

import java.io.StringReader;
import java.security.KeyPair;

import de.persosim.simulator.crypto.CryptoUtil;
import de.persosim.simulator.utils.HexString;

/**
 * @author amay
 *
 */
public class ProfileUB04 extends AbstractProfile {
	
	@Override
	public void setPersoDataContainer() {
		persoDataContainer = PersonalizationDataContainer.getDefaultContainer();
		persoDataContainer.setDg1PlainData("UB");
//		persoDataContainer.setDg3PlainData("20291031");
		persoDataContainer.setDg3PlainData("20340630");
		persoDataContainer.setDg4PlainData("PADDY");
		persoDataContainer.setDg5PlainData("Ó RUDAÍ");
		persoDataContainer.setDg6PlainData("MISTER BARISTA");
		persoDataContainer.setDg7PlainData("DR.HC.");
		persoDataContainer.setDg8PlainData("195309  ");
		persoDataContainer.setDg9PlainData("BALLYCLOUGH");
		persoDataContainer.setDg10PlainData("IRL");
		persoDataContainer.setDg17StreetPlainData("R3 3-5");
		persoDataContainer.setDg17CityPlainData("MANNHEIM");
		persoDataContainer.setDg17CountryPlainData("D");
		persoDataContainer.setDg17ZipPlainData("68161");
		persoDataContainer.setDg18PlainData("02760802220000");
		persoDataContainer.setEfCardAccess("");
		persoDataContainer.setEfCardSecurity("");
		persoDataContainer.setEfChipSecurity("");
		
		// UB do not have a MRZ, but BSI profiles have one. Therefore, this related class has one, too.
		String documentNumber = "00000UB04";
		String sex = "M";
		String mrzLine3 = "O<RUDAI<<PADDY<<<<<<<<<<<<<<<<";
		String mrz = persoDataContainer.createMrzFromDgs(documentNumber, sex, mrzLine3);
		persoDataContainer.setMrz(mrz);
		persoDataContainer.setEpassDg1PlainData(mrz);
		
		// unprivileged CA key
		persoDataContainer.addCaKeyPair(CryptoUtil.reconstructKeyPair(13,
				HexString.toByteArray("0467C26931380033AF00024BA205E8BC592426387A3AB77B38C5996852F3279092570A545BC66A440B2157307CA931CEC064F031AA0D14AC95391ED4B95C5F537E"),
				HexString.toByteArray("751B90F43087F3295E8BD811F8290F2504D06602DA8CD5A2EAAF53FB660C634C")),
				41, false);
		
		// privileged CA key
		persoDataContainer.addCaKeyPair(CryptoUtil.reconstructKeyPair(13,
				HexString.toByteArray("049A40D8601587E5728A8B78E258163EF1CF19B7867B8546864774A35CDDB3E21B38E7199775E0F7CC0EE7131B23000A946556D97AD71208B2746AFE9A397E0E8D"),
				HexString.toByteArray("18FD9A409F27A4DB55DAFBB5BE52FC8194A7EBB1D741C642EC5E10EC14047070")),
				45, true);
		
		// individual RI key - 1st sector public/private key pair (Sperrmerkmal)
		KeyPair riKeyPair1 = (KeyPair)PersonalizationFactory.unmarshal(new StringReader("""
				<java.security.KeyPair id="1">
				  <privateKey id="2">
				    <algorithm>ECDH</algorithm>
				    <value></value>
				  </privateKey>
				  <publicKey id="3">
				    <algorithm>ECDH</algorithm>
				    <value></value>
				  </publicKey>
				</java.security.KeyPair>						
		"""));
		persoDataContainer.addRiKeyPair(riKeyPair1, 1, false);

		// individual RI key - 2nd sector public/private key pair (Pseudonym)
		KeyPair riKeyPair2 = (KeyPair)PersonalizationFactory.unmarshal(new StringReader("""
				<java.security.KeyPair id="1">
				  <privateKey id="2">
				    <algorithm>ECDH</algorithm>
				    <value></value>
				  </privateKey>
				  <publicKey id="3">
				    <algorithm>ECDH</algorithm>
				    <value></value>
				  </publicKey>
				</java.security.KeyPair>						
		"""));
		persoDataContainer.addRiKeyPair(riKeyPair2, 2, true);
	}

}